INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Dynamikker der styrer familien', '20210927 16:30:00', 60, 3, 'Når vi bliver pressede, langer vi ud efter andre. Vi mister overblikket, og uhensigtsmæssige dynamikker kan blive styrende for de beslutninger, vi tager. Det kan ske både i familier og på arbejdspladser, og første skridt er at genkende de dynamikker, vi bliver fangede i. Oplægget vil introducere en model fra transaktionsanalysen kaldet ”Offer-Krænker-Frelser”. Deltagerne får et redskab til at tænke sig ud af klemte situationer og alternativer til at blive fanget i sådanne dynamikker. Workshoppen giver svar på Hvordan forstår vi uhensigtsmæssige dynamikker? Hvordan identificerer vi dem, når de opstår? Hvilke greb kan vi bruge til at komme ud af dem? Oplægsholderen præsenterer tre greb: Indholdsgrebet, mentaliseringsgrebet og rammegrebet til at gøre sig fri af dynamikkerne for en stund. Oplægget er både for fagfolk og pårørende.');
--Event_Id=20

