INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Autisme og isolation', '20210927 13:00:00', 55, 3, 'Alt for ofte sker det, at børn, unge og voksne med autisme bliver belastet i sådan en grad, at en overlevelses- eller mestringsstrategi er at isolere sig. At trække sig ind bag den lukkede dør, og derinde at skærme sig mod de krav og forventninger, som man ikke kan leve op til. Social isolation er en stor udfordring at tackle for både fagfolk og for forældre/ pårørende. Det kan være svært at vide, hvilke strategier man skal bruge for at hjælpe personen med at bryde isolationen. Man kan være bange for at gøre ting værre og man kan være bekymret for ikke at gøre nok. Denne workshop sætter fokus på autisme og social isolation og på de redskaber og metoder, der kan være med til at løsne op for en stagneret situation.');
--Event_Id=31

