INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Konflikthåndtering', '20210927 15:00:00', 55, 3, 'Studio 3, Konflikthåndtering, konfliktforebyggelse og Low Arousal er altsammen beskrivelser af, hvordan vi kan arbejde med at forebygge/forsøge at undgå, at vi utilsigtet er medvirkende til, at der opstår konflikter med de børn, unge og voksne, vi samarbejder med. Dette gælder både for fagpersoner og forældre.Studio 3 og Low Arousal har efterhånden en lang, god tradition og historie i Danmark. De som har arbejdet med dette helt fra starten, har mange positive erfaringer med at anvende disse strategier. I dette oplæg vil jeg gerne dele ud af nogle af de erfaringer og fortælle om Studio 3. Hvad er baggrunden for, at det virker så godt, og hvorfor mængden af konflikter og magtanvendelser falder de steder, hvor disse strategier benyttes.');
--Event_Id=37

