INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Perspektiver på skolevægring', '20210927 13:00:00', 55, 3, 'Ifølge Socialstyrelsen er skolevægring er den mest udbredte følgeproblematik for diagnosticerede børn og unge. Skolevægring og bekymrende skolefravær er et samfundsproblem, der påvirker mange familier, og samtidig udfordrer skoler og kommuner. Deltagerne får viden om og forståelse for børn og unge, som har problematisk skolefravær eller skolevægringsproblematikker, og konkrete redskaber, som kan bruges i hjemmet, skolen og samarbejdet. Oplægget vil blandt andet komme omkring følgende temaer: • Nyeste viden om skolevægring og autisme. • Overblik over fraværstyper og faktorer, der påvirker fraværet. • Indblik i, hvorfor der er behov for en grundig afdækning af bagvedliggende faktorer for at komme skolevægring til livs. Målgruppe: Fagfolk og forældre/pårørende');
--Event_Id=47

