INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Autisme og rusmidler – rusens funktion', '20210927 11:30:00', 55, 3, 'Der findes aktuelt ikke specialiserede behandlingstilbud rettet mod mennesker med autisme og misbrug. Erfaringer fra henholdsvis rusmiddelcentre og fra botilbud peger på, at borgere med autisme ofte har svært ved at indgå i den konventionelle misbrugsbehandling. I Specialområde Autisme har vi indsamlet viden og udviklet tiltag, der kan opkvalificere vores medarbejdere i arbejdet med at hjælpe målgruppen. I vores oplæg vil vi med udgangspunkt i misbrugspsykologi, autismefaglighed og konkrete erfaringer præsentere en forståelsesramme for arbejdet med mennesker med autisme og samtidigt misbrug af rusmidler. Vi henter vores erfaringer dels fra rusmiddelbehandlingen dels fra arbejdet med borgere i Specialområde Autismes botilbud. Vi vil i oplægget bl.a. beskæftige os med følgende: De primære grupper af rusmidler og deres virkning Hvordan påvirkes hjernen af euforiserende stoffer? Hvorfor udvikler nogle mennesker et misbrug? En socialpsykologisk og helheldsorienteret forståelse af rusens funktion og behandling af misbrug Rusmidlernes og rusens funktion specifikt hos mennesker med autisme Hvordan man kan afdække rusens funktion hos det enkelte menneske? Hvordan kan man tilrettelægge en indsats med baggrund i viden om misbrugets omfang og funktion hos det enkelte menneske? Workshoppen vil veksle mellem oplæg og plenumrefleksioner.');
--Event_Id=55

INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Autisme og rusmidler – rusens funktion', '20210928 11:00:00', 55, 3, 'Der findes aktuelt ikke specialiserede behandlingstilbud rettet mod mennesker med autisme og misbrug. Erfaringer fra henholdsvis rusmiddelcentre og fra botilbud peger på, at borgere med autisme ofte har svært ved at indgå i den konventionelle misbrugsbehandling. I Specialområde Autisme har vi indsamlet viden og udviklet tiltag, der kan opkvalificere vores medarbejdere i arbejdet med at hjælpe målgruppen. I vores oplæg vil vi med udgangspunkt i misbrugspsykologi, autismefaglighed og konkrete erfaringer præsentere en forståelsesramme for arbejdet med mennesker med autisme og samtidigt misbrug af rusmidler. Vi henter vores erfaringer dels fra rusmiddelbehandlingen dels fra arbejdet med borgere i Specialområde Autismes botilbud. Vi vil i oplægget bl.a. beskæftige os med følgende: De primære grupper af rusmidler og deres virkning Hvordan påvirkes hjernen af euforiserende stoffer? Hvorfor udvikler nogle mennesker et misbrug? En socialpsykologisk og helheldsorienteret forståelse af rusens funktion og behandling af misbrug Rusmidlernes og rusens funktion specifikt hos mennesker med autisme Hvordan man kan afdække rusens funktion hos det enkelte menneske? Hvordan kan man tilrettelægge en indsats med baggrund i viden om misbrugets omfang og funktion hos det enkelte menneske? Workshoppen vil veksle mellem oplæg og plenumrefleksioner.');
