INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Virtual Reality', '20210927 13:30:00', 55, 3, 'Virtual Reality som redskab til social træning og eksponering til voksne med autisme Med Virtual Reality (VR) får vi nye muligheder for at arbejde med social træning og eksponering, og det afprøver Specialområde Autisme i efteråret 2019 igennem seks borgerforløb á otte uger. De seks borgere samarbejder med udvalgte medarbejdere på fem afdelinger. Social træning er ofte en væsentlig del af den pædagogiske indsats, som Specialområde Autismes medarbejdere udfører i samarbejdet med borgerne. På samme måde anvender vi eksponering som en væsentlig komponent i kognitiv adfærdsterapi til behandling af angst og angstlignende tilstande. VR åbner for unikke muligheder for at graduere sociale sammenhænge og gradvist eksponere for angstfremkaldende situationer, som kan være udfordrende i den ”virkelige verden”. Det er dermed vores håb, at VR kan anvendes som et læringsværktøj, som kan gøre borgere bedre i stand til at håndtere sociale og angstfremkaldende situationer. Fælles for alle forløb er, at der er mindst én ugentlig VR-sessioner med borgeren over en periode på otte uger og at der bliver arbejdet med konkrete individuelle mål som evalueres løbende. Som en del af evalueringen af projektet udføres individuelle interview med de involverede borgere og et fokusgruppeinterview med medarbejderne. Projektet er blevet støttet økonomisk af Socialområdes pulje til forskning og udvikling i Region Midtjylland. På workshoppen kan du høre om Specialområde Autismes erfaringer, resultater og faglige overvejelser i forbindelse med at anvende VR til eksponering og social træning.');
--Event_Id=57

INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Virtual Reality', '20210928 10:00:00', 55, 3, 'Virtual Reality som redskab til social træning og eksponering til voksne med autisme Med Virtual Reality (VR) får vi nye muligheder for at arbejde med social træning og eksponering, og det afprøver Specialområde Autisme i efteråret 2019 igennem seks borgerforløb á otte uger. De seks borgere samarbejder med udvalgte medarbejdere på fem afdelinger. Social træning er ofte en væsentlig del af den pædagogiske indsats, som Specialområde Autismes medarbejdere udfører i samarbejdet med borgerne. På samme måde anvender vi eksponering som en væsentlig komponent i kognitiv adfærdsterapi til behandling af angst og angstlignende tilstande. VR åbner for unikke muligheder for at graduere sociale sammenhænge og gradvist eksponere for angstfremkaldende situationer, som kan være udfordrende i den ”virkelige verden”. Det er dermed vores håb, at VR kan anvendes som et læringsværktøj, som kan gøre borgere bedre i stand til at håndtere sociale og angstfremkaldende situationer. Fælles for alle forløb er, at der er mindst én ugentlig VR-sessioner med borgeren over en periode på otte uger og at der bliver arbejdet med konkrete individuelle mål som evalueres løbende. Som en del af evalueringen af projektet udføres individuelle interview med de involverede borgere og et fokusgruppeinterview med medarbejderne. Projektet er blevet støttet økonomisk af Socialområdes pulje til forskning og udvikling i Region Midtjylland. På workshoppen kan du høre om Specialområde Autismes erfaringer, resultater og faglige overvejelser i forbindelse med at anvende VR til eksponering og social træning.');
--Event_Id=58

INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Forældre med et autistisk barn', '20210927 14:00:00', 55, 3, 'Når man får et barn med særlige behov, ændres familiens situation og livsgrundlag på alle basale parametre. Det påvirker daglige rutiner i hjemmet, parforholdet, forholdet til omgivende familie og netværk, kontakt til venner og deltagelsen på arbejdsmarkedet. I workshoppen vil vi anvende Hejlskov-Uhrskovs stressmodel (Elven, 2010) som forståelsesramme for de udfordringer, forældre til børn og unge med autisme kan opleve i forhold til at fastholde et familieliv og samtidig støtte deres barn med diagnose. Vi vil udfolde forskellige faktorer, der kan gøre sig gældende, og belyse, hvordan disse samlet bidrager til et øget belastningsniveau for både familien og den enkelte forælder. Vi kommer ind på følgende emner Sorgprocesser i forhold til ’det tabte liv’, f.eks. familiedrømmen og tab af egen-identitet Den usynlige diagnose: når barnets vanskeligheder er usynlige for omgivelserne Hvad det betyder, når hverdagens rutiner bliver udfordrede Udfordringer for parforholdet Barn-forældre relation Søskendeproblematikker Samarbejde med fagprofessionelle og det at agere i ’systemet’ Påvirkning af eget funktionsniveau Med oplægget ønsker vi at dele vores erfaringer fra vores daglige arbejde med forældre til børn med autismespektrumforstyrrelser og herigennem skabe øget forståelse for deres situation. Målgrupper Pårørende til personer med handicap/ særlige behov Fagpersoner, som arbejder med familier med særlige behov');
--Event_Id=59

INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Forældre med et autistisk barn', '20210928 14:00:00', 55, 3, 'Når man får et barn med særlige behov, ændres familiens situation og livsgrundlag på alle basale parametre. Det påvirker daglige rutiner i hjemmet, parforholdet, forholdet til omgivende familie og netværk, kontakt til venner og deltagelsen på arbejdsmarkedet. I workshoppen vil vi anvende Hejlskov-Uhrskovs stressmodel (Elven, 2010) som forståelsesramme for de udfordringer, forældre til børn og unge med autisme kan opleve i forhold til at fastholde et familieliv og samtidig støtte deres barn med diagnose. Vi vil udfolde forskellige faktorer, der kan gøre sig gældende, og belyse, hvordan disse samlet bidrager til et øget belastningsniveau for både familien og den enkelte forælder. Vi kommer ind på følgende emner Sorgprocesser i forhold til ’det tabte liv’, f.eks. familiedrømmen og tab af egen-identitet Den usynlige diagnose: når barnets vanskeligheder er usynlige for omgivelserne Hvad det betyder, når hverdagens rutiner bliver udfordrede Udfordringer for parforholdet Barn-forældre relation Søskendeproblematikker Samarbejde med fagprofessionelle og det at agere i ’systemet’ Påvirkning af eget funktionsniveau Med oplægget ønsker vi at dele vores erfaringer fra vores daglige arbejde med forældre til børn med autismespektrumforstyrrelser og herigennem skabe øget forståelse for deres situation. Målgrupper Pårørende til personer med handicap/ særlige behov Fagpersoner, som arbejder med familier med særlige behov');
