INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Low arousal tilgang… eller hvad?', '20210927 14:00:00', 55, 3, 'Low arousal tilgang er blevet en udbredt tilgang indenfor autismeområdet. Der er jævnligt artikler i fagblade, hvor low arousal tilgangen roses og får æren får store fald i antallet af magtanvendelser og jeg er personligt stor fortaler for tilgangen. På den anden side oplever jeg også jævnligt markant modstand på low arousal tilgang, med henvisning til, at man ikke må stille krav, når man arbejder med low arousal tilgang. Det er min oplevelse at low arousal tilgang i høj grad er misforstået og, at den er blevet noget mange italesætter at arbejde med, men egentligt ikke ved hvad dækker over. Low arousal tilgang handler kort sagt om håndtering og nedtrapning af problematisk adfærd, hvor det vigtigste værktøj er dig selv. Vi kommer bl.a. til at tale om amygdala (populært sagt, hjernens alarmcentral) og affekt/ stresssmitte samt hvordan det at blive kapret af sine følelser ikke blot sker for mennesker med autisme – men derimod kan ske for os alle. Ud over at uddybe, hvad low arousal tilgang er, vil jeg på workshoppen også tale om hvad low arousal tilgang ikke er. Eksempelvis bør low arousal tilgang ikke være den primære pædagogik i hverdagen eller et argument for, at man ikke kan stille krav i hverdagen. På workshoppen vil der være inspiration til, hvordan man kan arbejde med low arousal tilgang, og dermed hvordan man måske kan mindske mængden og intensiteten af konflikter.');
--Event_Id=61

INSERT INTO _Event (_Name, StartTime, Duration, Room_Id, _Description)
VALUES ('Low arousal tilgang… eller hvad?', '20210928 14:00:00', 55, 3, 'Low arousal tilgang er blevet en udbredt tilgang indenfor autismeområdet. Der er jævnligt artikler i fagblade, hvor low arousal tilgangen roses og får æren får store fald i antallet af magtanvendelser og jeg er personligt stor fortaler for tilgangen. På den anden side oplever jeg også jævnligt markant modstand på low arousal tilgang, med henvisning til, at man ikke må stille krav, når man arbejder med low arousal tilgang. Det er min oplevelse at low arousal tilgang i høj grad er misforstået og, at den er blevet noget mange italesætter at arbejde med, men egentligt ikke ved hvad dækker over. Low arousal tilgang handler kort sagt om håndtering og nedtrapning af problematisk adfærd, hvor det vigtigste værktøj er dig selv. Vi kommer bl.a. til at tale om amygdala (populært sagt, hjernens alarmcentral) og affekt/ stresssmitte samt hvordan det at blive kapret af sine følelser ikke blot sker for mennesker med autisme – men derimod kan ske for os alle. Ud over at uddybe, hvad low arousal tilgang er, vil jeg på workshoppen også tale om hvad low arousal tilgang ikke er. Eksempelvis bør low arousal tilgang ikke være den primære pædagogik i hverdagen eller et argument for, at man ikke kan stille krav i hverdagen. På workshoppen vil der være inspiration til, hvordan man kan arbejde med low arousal tilgang, og dermed hvordan man måske kan mindske mængden og intensiteten af konflikter.');
